    private void A66ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_A66ActionPerformed
        try {
            int cantidadCorteCaballero = Integer.parseInt(A1.getText());
            int cantidadCorteDama = Integer.parseInt(A2.getText());
            int cantidadTintura = Integer.parseInt(A3.getText());
            int cantidadPeinado = Integer.parseInt(A4.getText());

            CorteCaballero corteCaballero = new CorteCaballero(cantidadCorteCaballero);
            CorteDama corteDama = new CorteDama(cantidadCorteDama);
            Tintura tintura = new Tintura(cantidadTintura);
            Peinado peinado = new Peinado(cantidadPeinado);

            double costoTotalCorteCaballero = corteCaballero.calcularCostoTotal();
            double costoTotalCorteDama = corteDama.calcularCostoTotal();
            double costoTotalTintura = tintura.calcularCostoTotal();
            double costoTotalPeinado = peinado.calcularCostoTotal();

            A11.setText("" + costoTotalCorteCaballero);
            A22.setText("" + costoTotalCorteDama);
            A33.setText("" + costoTotalTintura);
            A44.setText("" + costoTotalPeinado);

            double costoTotal = costoTotalCorteCaballero + costoTotalCorteDama + costoTotalTintura + costoTotalPeinado;

            A66.setText("" + costoTotal);

            int totalCantidadServicios = cantidadCorteCaballero + cantidadCorteDama + cantidadTintura + cantidadPeinado;

            A5.setText("" + totalCantidadServicios);
        } catch (NumberFormatException e) {
            A66.setText("Error: Ingrese números válidos");
        }


    }//GEN-LAST:event_A66ActionPerformed

