    private void VerTotalesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VerTotalesActionPerformed
        try {
            double valorA1 = Double.parseDouble(A66.getText());
            double valorB1 = Double.parseDouble(B66.getText());
            double valorC1 = Double.parseDouble(C66.getText());
            double valorD1 = Double.parseDouble(D66.getText());
            
            ServicioCorte servicioA = new ServicioCorte(1, valorA1); 
            ServicioCorte servicioB = new ServicioCorte(1, valorB1);
            ServicioCorte servicioC = new ServicioCorte(1, valorC1);
            ServicioCorte servicioD = new ServicioCorte(1, valorD1);
            
            double costoTotalA = servicioA.calcularCostoTotal();
            double costoTotalB = servicioB.calcularCostoTotal();
            double costoTotalC = servicioC.calcularCostoTotal();
            double costoTotalD = servicioD.calcularCostoTotal();
            
            double suma = costoTotalA + costoTotalB + costoTotalC + costoTotalD;

            String mensaje = "El total es: " + suma;

            JOptionPane.showMessageDialog(null, mensaje, "El total por todos los peluqueros es :", JOptionPane.INFORMATION_MESSAGE);
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Alguno de los valores en los JLabel no es un número entero válido.", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
        
        try {
            int valorZ1 = Integer.parseInt(A5.getText());
            int valorZ2 = Integer.parseInt(B5.getText());
            int valorZ3 = Integer.parseInt(C5.getText());
            int valorZ4 = Integer.parseInt(D5.getText());

            int suma = valorZ1 + valorZ2 + valorZ3 + valorZ4;

            String mensaje = "El total de servicios fue de :" + suma;

            JOptionPane.showMessageDialog(null, mensaje, "El total de Servicios", JOptionPane.INFORMATION_MESSAGE);
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Alguno no es valido.", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
        

    }//GEN-LAST:event_VerTotalesActionPerformed

